# Set JAVA HOME export JAVA_HOME={{java64_home}}
# Add hadoop configuration directory into classpath export HADOOP_CONF_DIR={{hadoop_config_dir}}
# Setup the environment for SparkGraphComputer # Add yarn and spark lib and config into classpath for SparkGraphComputer. export YARN_HOME={{yarn_home_dir}} export YARN_CONF_DIR=$HADOOP_CONF_DIR export SPARK_HOME={{spark_home_dir}} export SPARK_CONF_DIR={{spark_config_dir}} export JANUSGRAPH_HOME={{janusgraph_home_dir}} export CLASSPATH=$CLASSPATH:$HADOOP_CONF_DIR:$SPARK_CONF_DIR:$JANUSGRAPH_HOME/conf
#add hbase configuration directory into classpath if ([ -d "{{hbase_config_dir}}" ]); then export HBASE_CONF_DIR={{hbase_config_dir}} export CLASSPATH=$CLASSPATH:$HBASE_CONF_DIR fi
if ([[ ! -d "{{janusgraph_ext_spark_plugin_dir}}" ]] && [[ -d "$SPARK_HOME/jars" ]]); then for jar in $SPARK_HOME/jars/*.jar; do if ([[ $jar != */guava*.jar ]] && [[ $jar != */slf4j-log4j12*.jar ]] && [[ $jar != */spark-core*.jar ]]) ;then CLASSPATH=$CLASSPATH:$jar fi done fi
export CLASSPATH
# Add iop.version and native lib in java opt for hadoop config. export IOP_JAVA_OPTIONS="$JAVA_OPTIONS -D{{platform_name}}.version={{full_stack_version}} -Djava.library.path={{hadoop_lib_native_dir}}"
{% if security_enabled -%} export JVMFLAGS="-Djava.security.auth.login.config={{janusgraph_solr_client_jaas_file}}" export IOP_JAVA_OPTIONS="$IOP_JAVA_OPTIONS $JVMFLAGS" {% endif %}
source "$HADOOP_CONF_DIR"/hadoop-env.sh export HADOOP_GREMLIN_LIBS=$JANUSGRAPH_HOME/lib export JANUSGRAPH_LOGDIR={{janusgraph_log_dir}}
